package day06

import java.lang.IllegalArgumentException

fun main() {
    println(findStart(input, 4))
    println(findStart(input, 14))
}

fun findStart(input: String, numberOfDistinctChars: Int): Int {
    input.forEachIndexed { index, _ ->
        if (areDistinct(input, index, numberOfDistinctChars)) {
            return index + numberOfDistinctChars
        }
    }
    throw IllegalArgumentException()
}

fun areDistinct(value: String, startIndex: Int, numberOfCharacters: Int): Boolean {
    val distinctCharacters = mutableSetOf<Char>()

    value.substring(startIndex, startIndex + numberOfCharacters).forEach { distinctCharacters.add(it) }

    return distinctCharacters.size == numberOfCharacters
}

private const val input =
    ""